import React, { useEffect, useRef, useState } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader';

const CanvasScene = () => {
  const mountRef = useRef(null);
  const [loading, setLoading] = useState(true);
  const characterRef = useRef(null);
  const cameraRef = useRef(null);
  const controlsRef = useRef(null);
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);

  // Function to focus camera on character
  const focusOnCharacter = () => {
    if (!characterRef.current || !cameraRef.current || !controlsRef.current) return;
    
    const box = new THREE.Box3().setFromObject(characterRef.current);
    const center = box.getCenter(new THREE.Vector3());
    const size = box.getSize(new THREE.Vector3());
    const maxDim = Math.max(size.x, size.y, size.z);
    
    // Position camera at a good distance based on model size
    const distance = maxDim * 3;
    cameraRef.current.position.set(
      center.x + distance,
      center.y + distance * 0.5,
      center.z + distance
    );
    cameraRef.current.lookAt(center);
    controlsRef.current.target.copy(center);
  };

  // Function to focus camera on floor
  const focusOnFloor = () => {
    if (!cameraRef.current || !controlsRef.current) return;
    
    cameraRef.current.position.set(20, 20, 20);
    cameraRef.current.lookAt(0, 0, 0);
    controlsRef.current.target.set(0, 0, 0);
  };

  // Function to initialize the scene
  const initScene = () => {
    console.log('Initializing scene...');
    
    // Scene setup
    const scene = new THREE.Scene();
    sceneRef.current = scene;
    scene.background = new THREE.Color(0x87ceeb); // Sky blue background
    console.log('Scene created');

    // Camera setup
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    cameraRef.current = camera;
    camera.position.set(20, 20, 20);
    camera.lookAt(0, 0, 0);
    console.log('Camera created and positioned');

    // Renderer setup
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    rendererRef.current = renderer;
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    mountRef.current.appendChild(renderer.domElement);
    console.log('Renderer created and mounted');

    // Create green floor
    const floorSize = 30;
    const floorGeometry = new THREE.PlaneGeometry(floorSize, floorSize);
    const floorMaterial = new THREE.MeshStandardMaterial({
      color: 0x2ecc71, // Bright green color
      roughness: 0.8,
      metalness: 0.2
    });
    const floor = new THREE.Mesh(floorGeometry, floorMaterial);
    floor.rotation.x = -Math.PI / 2; // Rotate to be horizontal
    floor.receiveShadow = true;
    scene.add(floor);
    console.log('Floor created and added to scene');

    // Add grid helper for better visualization
    const gridHelper = new THREE.GridHelper(floorSize, 30, 0x000000, 0x000000);
    scene.add(gridHelper);
    console.log('Grid helper added');

    // Add axes helper
    const axesHelper = new THREE.AxesHelper(5);
    scene.add(axesHelper);
    console.log('Axes helper added');

    // Lighting setup
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
    directionalLight.position.set(10, 20, 10);
    directionalLight.castShadow = true;
    directionalLight.shadow.mapSize.width = 2048;
    directionalLight.shadow.mapSize.height = 2048;
    scene.add(directionalLight);
    console.log('Lights added');

    // Camera controls
    const controls = new OrbitControls(camera, renderer.domElement);
    controlsRef.current = controls;
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;
    controls.minDistance = 5;
    controls.maxDistance = 50;
    controls.maxPolarAngle = Math.PI / 2; // Prevent going below floor
    console.log('Controls created');

    // Load character model
    const loader = new OBJLoader();
    console.log('Starting to load character model...');
    loader.load(
      '/assets/models/characters/raku_obj.obj',
      (object) => {
        console.log('Character model loaded');
        characterRef.current = object;
        
        // Center the model
        const box = new THREE.Box3().setFromObject(object);
        const center = box.getCenter(new THREE.Vector3());
        object.position.sub(center);

        // Scale the model to be more visible
        const size = box.getSize(new THREE.Vector3());
        const maxDim = Math.max(size.x, size.y, size.z);
        const scale = 8 / maxDim; // Make it 8 units tall
        object.scale.multiplyScalar(scale);

        // Position at the edge of the floor
        object.position.set(-floorSize/2 + 2, 0, -floorSize/2 + 2);

        // Add materials to all meshes
        object.traverse((child) => {
          if (child instanceof THREE.Mesh) {
            child.castShadow = true;
            child.receiveShadow = true;
            child.material = new THREE.MeshStandardMaterial({
              color: 0xff0000, // Bright red for visibility
              roughness: 0.5,
              metalness: 0.5
            });
          }
        });

        scene.add(object);
        console.log('Character added to scene');
        setLoading(false);
        focusOnCharacter(); // Focus on character when loaded
      },
      (xhr) => {
        console.log((xhr.loaded / xhr.total * 100) + '% loaded');
      },
      (error) => {
        console.error('Error loading model:', error);
        setLoading(false);
      }
    );

    // Handle window resize
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', handleResize);

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    };
    animate();
    console.log('Animation loop started');

    // Initial focus on floor
    focusOnFloor();
    console.log('Initial focus set on floor');

    return () => {
      console.log('Cleaning up scene...');
      window.removeEventListener('resize', handleResize);
      if (mountRef.current && renderer.domElement) {
        mountRef.current.removeChild(renderer.domElement);
      }
      scene.clear();
    };
  };

  useEffect(() => {
    console.log('Component mounted');
    if (!mountRef.current) {
      console.log('Mount ref not available');
      return;
    }
    
    const cleanup = initScene();
    return cleanup;
  }, []);

  return (
    <div style={{ width: '100%', height: '100%', position: 'relative' }}>
      <div ref={mountRef} style={{ width: '100%', height: '100%' }} />
      {loading && (
        <div style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          color: 'white',
          fontSize: '20px',
          textShadow: '2px 2px 4px rgba(0,0,0,0.5)'
        }}>
          Loading Character...
        </div>
      )}
      <div style={{
        position: 'absolute',
        bottom: '20px',
        right: '20px',
        display: 'flex',
        gap: '10px'
      }}>
        <button
          onClick={focusOnFloor}
          style={{
            padding: '10px 20px',
            backgroundColor: 'rgba(255, 255, 255, 0.8)',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            zIndex: 1000
          }}
        >
          Focus on Floor
        </button>
        <button
          onClick={focusOnCharacter}
          style={{
            padding: '10px 20px',
            backgroundColor: 'rgba(255, 255, 255, 0.8)',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            zIndex: 1000
          }}
        >
          Focus on Character
        </button>
      </div>
    </div>
  );
};

export default CanvasScene;
